import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [active, setActive] = useState(false);
  const [text, setText] = useState('CONFIRM')
  const [code, setCode] = useState(null)

  function getQueryParams(url) {
    const queryString = window.location.search;

    // Create a new URLSearchParams object with the query string
    const urlParams = new URLSearchParams(queryString);

    // Initialize an empty object to store the parsed parameters
    const parsedParams = {};

    // Loop through each parameter in the URLSearchParams and populate the object
    for (const [key, value] of urlParams.entries()) {
      // Try to parse the value as JSON
      let parsedValue;
      try {
        parsedValue = JSON.parse(value);
      } catch (error) {
        // If parsing fails, assign the value as is
        parsedValue = value;
      }
      parsedParams[key] = parsedValue;
    }

    console.log(parsedParams.pa);    
  }

  const handleConfirm = () => {
    const queryParams = getQueryParams(document.location.href);
    setCode(queryParams);
    setActive(true)
    setText('CONFIRMED')
    setTimeout(()=> {
      setActive(false)
      setText('CONFIRM')
    },5000)
  }
console.log(getQueryParams(document.location.href))
  return (
    <div className="page">
      <header></header>
      <main>
        <div className='logo-main'></div>
        <p className='title'>Confirm payment</p>
        <p className={`${active ? 'active' : ''}`}>
          <button
            className={`waves`}
            onClick={handleConfirm}
          >
            <span>{text}</span>
          </button>
        </p>

      </main>
      <footer>
        <div className='logo-footer'>
        </div>
      </footer>
    </div>
  );
}

export default App;
